import streamlit as st import math
def initialize_pattern_state(): """Initialize the session state for pattern parameters if not already present.""" if 'pattern_params' not in st.session_state: st.session_state.pattern_params = { 'frequencies': [40.0] * 4, # Store up to 4 frequencies 'angles': [i * (180 / 4) for i in range(4)], # Store up to 4 angles 'thicknesses': [0.5] * 4, # Store up to 4 thicknesses 'circle_positions': [(0, 0)] * 4, # Store up to 4 circle positions 'R_values': [0.0] * 4, # Store R values for circle positions 'theta_values': [0.0] * 4 # Store theta values for circle positions }
def get_slider_key(param_type, index): """Generate a unique key for each slider""" return f"{param_type}_{index}"
def slider_callback(param_type, index): """Update session state when slider changes""" key = get_slider_key(param_type, index) if key in st.session_state: st.session_state.pattern_params[param_type][index] = st.session_state[key]
def get_input_controls(): initialize_pattern_state()
# First row: Pattern type, view mode, and intensity threshold col1, col2, col3a, col3b = st.columns([1, 1.5, 1, 0.5]) with col1: pattern_type = st.selectbox("Pattern Type", ["2 Vertical Grids", "3 Vertical Grids", "4 Vertical Grids", "2 Concentric Circles", "2 Dot Grids", "3 Dot Grids", "2 Inverted Dot Grids (Intensity in frequency domain incorrect)", "3 Inverted Dot Grids (Intensity in frequency domain incorrect)))"]) with col2: view_mode = st.radio("View Mode", ["Pattern & Frequency", "Fourier Analysis"], horizontal=True)
is_concentric = "Concentric" in pattern_type num_patterns = int(pattern_type[0]) slider_cols = st.columns(num_patterns + 2)
frequencies = [] angles = [] thicknesses = [] circle_positions = []
for i in range(num_patterns): with slider_cols[i]: st.write(f"{'Circle' if is_concentric else 'Grid'} {i+1}")
if is_concentric: # Frequency slider freq_key = get_slider_key('frequencies', i) frequencies.append( st.slider("Frequency", 10.0, 100.0, value=st.session_state.pattern_params['frequencies'][i], key=freq_key, on_change=slider_callback, args=('frequencies', i), step=0.1) )
# Thickness slider thick_key = get_slider_key('thicknesses', i) thickness = st.slider("Thickness", 0.1, 0.9, value=st.session_state.pattern_params['thicknesses'][i], key=thick_key, on_change=slider_callback, args=('thicknesses', i), step=0.1) thicknesses.append(thickness)
if i > 0: # R slider r_key = get_slider_key('R_values', i) R = st.slider("R", 0.0, 100.0, value=st.session_state.pattern_params['R_values'][i], key=r_key, on_change=slider_callback, args=('R_values', i), step=1.0)
# Theta slider theta_key = get_slider_key('theta_values', i) theta = st.slider("Theta", -180.0, 180.0, value=st.session_state.pattern_params['theta_values'][i], key=theta_key, on_change=slider_callback, args=('theta_values', i), step=1.0)
pos_x = R * math.cos(math.radians(theta)) pos_y = R * math.sin(math.radians(theta)) circle_positions.append((pos_x, pos_y)) else: circle_positions.append((0, 0)) angles.append(0) else: # Frequency slider freq_key = get_slider_key('frequencies', i) frequencies.append( st.slider("Frequency", 10.0, 100.0, value=st.session_state.pattern_params['frequencies'][i], key=freq_key, on_change=slider_callback, args=('frequencies', i), step=0.1) )
# Angle slider angle_key = get_slider_key('angles', i) angle = st.slider("Angle", 0.0, 360.0, value=st.session_state.pattern_params['angles'][i], key=angle_key, on_change=slider_callback, args=('angles', i), step=1.0) angles.append(angle)
# Thickness slider thick_key = get_slider_key('thicknesses', i) thickness = st.slider("Thickness", 0.1, 0.9, value=st.session_state.pattern_params['thicknesses'][i], key=thick_key, on_change=slider_callback, args=('thicknesses', i), step=0.1) thicknesses.append(thickness)
circle_positions.append((0, 0))
# Visibility and window controls (these don't need persistence) with slider_cols[-2]: st.write("Visibility") n_harmonics = st.slider("Number of Harmonics (1 for complex patterns)", 1, 10, 4, 1) intensity_threshold = st.slider("Intensity Threshold (above 0.1 for complex patterns)", 0.0, 0.5, 0.05, 0.01)
with slider_cols[-1]: st.write("Window") window_half_size = st.slider("Range", 5.0, 200.0, 100.0, 5.0) visibility_radius = st.slider("Radius", 1.0, 200.0, 100.0, 1.0)
return (pattern_type, frequencies, angles, thicknesses, circle_positions, visibility_radius, window_half_size, view_mode, intensity_threshold, n_harmonics)

import numpy as np

def create_pattern(size: int, frequency: float, angle: float, thickness: float, 
                  pattern_type: str, circle_position: tuple = (0, 0)) -> np.ndarray:
    x = np.linspace(-size/2, size/2, size)
    y = np.linspace(-size/2, size/2, size)
    X, Y = np.meshgrid(x, y)
    
    if 'Concentric' in pattern_type:
        # Existing concentric circle logic...
        X_shifted = X - circle_position[0]
        Y_shifted = Y - circle_position[1]
        R = np.sqrt(X_shifted**2 + Y_shifted**2)
        period = size / frequency
        return (R % period) < (period * (1-thickness))
    else:
        theta = np.radians(angle)
        X_rot = X * np.cos(theta) + Y * np.sin(theta)
        Y_rot = -X * np.sin(theta) + Y * np.cos(theta)
        
        if 'Dot' in pattern_type:
            pattern = ((X_rot % (size/frequency) < (size/frequency) * (1-thickness)) & 
                      (Y_rot % (size/frequency) < (size/frequency) * (1-thickness)))
            return ~pattern if 'Inverted' in pattern_type else pattern
        else:
            return X_rot % (size/frequency) < (size/frequency) * (1-thickness)

